package JavaBasics;

public class Continue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Continue 
		for (int i = 0; i < 10; i++)
		{
		if (i == 4)
		{
		continue;
		}
		System.out.println(i);

	}
		//Break
		for (int i = 0; i < 10; i++)
		{
		if (i == 4)
		{
		break;
		}
		System.out.println(i);

}
}
}
