package Task6CorejavaTask;

public class FindingGreatestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// for find greatest number of 3 numbers
				int a1=300;
				int b1=800;
				int c1=500;
				 // we need to use nested if else condition here
				if(a1>b1&&a1>c1) {
					System.out.println(" a1 is greatest number");
				}
				else if (b1>a1&&b1>c1) {
					System.out.println("b1 is greatest number");
				}
				else {
					System.out.println("c1 is greatest number");
					System.out.println("---------------");
				}

	}

}
